// 心臓の鼓動のようなアニメーション
const cycle = 100; // 1周期のフレーム数
let count = 0; // 何フレーム目か
let size = 50;

function setup(){
  createCanvas(200, 200);
  count = 0;
}

function draw(){
  background(160, 192, 255); // 前回の描画を上塗りして消す
  
  let speed = 1; // アニメーションの速さ
  speed += 1 
  // BLANK[2]
  count = (count + speed) % cycle;

  let size = 50;
  if(count < cycle / 2){
    size += 1;
  }else{
    size  -= 1
  }
  // BLANK[1] 1周期の前半は size が大きくなり、後半は小さくなる
  ellipse(width / 2, height / 2, size);
}

