  const size = height * 0.1; // キャラクターのサイズ


  // 地面を描く
  const groundY = height * 0.8;
  fill(64, 192, 64);
  rect(0, groundY, width, height - groundY);

  // BLANK[1] キャラクターの左右移動
  let moveSpeed = 0.5 // 左右に動かす時の基本的な速度を設定

  // SHIFTキーで加速
  if(keyIsDown(SHIFT)){ 
    moveSpeed = 1.5 
  }

  if(keyIsDown(LEFT_ARROW)){ vx -= moveSpeed; }
  if(keyIsDown(RIGHT_ARROW)){ x += moveSpeed; }
  
  // BLANK[2] 重力とジャンプ
  // 重力をかける
  vy += g;
  vy = constrain( vy, -20, 20 );
  if(x < 0 || x > width){ vx = -1 * vx; }
  if(y < 0 || y > height - groundY - size / 2 ){ vy = -1 * vy; }
  // スペースキーを押したときにジャンプ
  if(keyIsDown(' '.charCodeAt(0))){ vy = -10; }

  // 速くなりすぎないように制限
  vx = constrain( vx, -20, 20 );
  vy = constrain( vy, -20, 20 );

  // 位置を更新
  x += vx;
  y += vy;

  // キャラクターを描く
  fill(0);
  ellipse( x, y, size, size );
}

