function setup() {
  createCanvas(570, 300);
  noStroke();
  background(255);

  const d = height / 13; // 縞1本の太さ
  const blue = color(0, 40, 104);
  const red = color(191, 10, 48)

  // 縞を描く
  for(let i = 0; i < 13; i++){
    // BLANK[1] ヒント：縞の色を交互に変えるには2で割った余りを使おう
    // 先に色を指定
    if(i % 2 == 0){
      fill(red);
    }else{
      fill(255);
    }
    rect(0, i * d, width, d);
  }

  // 十字を描く
  // 十字のベース（背景の正方形）の準備
  const size = d * 7;
  fill(blue);
  rect(0, 0, size, size);
  // 星を描く
  for(let i = 0; i < 13; i++){
    const theta = TWO_PI * i / 13;   // TWO_PI は円周率の2倍
    const x = 100 + cos(theta) * 50; // 関数 cos の戻り値を使用
    const y = 100 + sin(theta) * 50; // 関数 sin の戻り値を使用
    ellipse(x, y, 10); // 関数 ellipse の副作用で円が描画される
    
  // 星マークを描く関数を呼び出す
  noStroke();
  star(x, y, 10); // 星マークを描く関数を呼び出す

    function star(cx, cy, r){
      beginShape();    // 点つなぎを始める
      for(let i = 0; i < 5; i++){
        const theta = TWO_PI * i * 2 / 5 - HALF_PI;
        const x = cx + cos(theta) * r;
        const y = cy + sin(theta) * r;
        vertex(x, y);  // 次につなぐ点を１つ増やす
      }
      endShape(CLOSE); // 点つなぎを終わる
    }
  }
}

